import { useState } from 'react';
import { Col, Container, Nav, Navbar, Row } from 'react-bootstrap'; //
import { Link, Route, Routes } from 'react-router-dom';
import './App.css';
import data from './data.js';

function App() {
    let [art] = useState(data);

    return (
        <div className='App'>
            <Navbar bg='light' variant='light' className='Navbar'>
                <Container>
                    <Navbar.Brand href='#home'>일시불</Navbar.Brand>
                    <Nav className='me-auto' className='Navi'>
                        {/* <Nav.Link href='#home'>홈</Nav.Link> */}
                        <Link to='/'>홈</Link>
                        <Nav.Link href='#features'>찾아보기</Nav.Link>
                        {/* <Nav.Link href='#pricing'>더보기</Nav.Link> */}
                        <Link to='/detail'>더보기</Link>
                    </Nav>
                </Container>
            </Navbar>

            <Routes>
                <Route
                    path='/'
                    element={
                        <>
                            <div className='Main-bg'></div>
                            <Container>
                                <Row className='row'>
                                    {art.map((a, i) => {
                                        return <MidArt art={art[i]}></MidArt>;
                                    })}
                                </Row>
                            </Container>
                        </>
                    }
                />
                <Route
                    path='/detail'
                    element={
                        <>
                            {' '}
                            <div className='container'>
                                <div className='row'>
                                    <div className='col-top-1'>
                                        <img
                                            src='https://codingapple1.github.io/shop/shoes1.jpg'
                                            width='100%'
                                        />
                                    </div>
                                    <div className='col-top-1'>
                                        <h4 className='pt-5'>상품명</h4>
                                        <p>상품설명</p>
                                        <p>120000원</p>
                                        <button className='btn btn-danger'>
                                            주문하기
                                        </button>
                                    </div>
                                </div>
                            </div>
                            );
                        </>
                    }
                />
            </Routes>
        </div>
    );
}
function MidArt(props) {
    return (
        <Col className='col-top-1'>
            <img src={props.art.src} width='80%' height='65%' />
            <h4>{props.art.title}</h4>
            <p>{props.art.content}</p>
        </Col>
    );
}

export default App;
