import './App.css';

function Detail(props) {
    return (
        <div className='container'>
            <div className='row'>
                <div className='col-top-1'>
                    <img src={props.art[props.art.id].src} width='100%' />
                </div>
                <div className='col-top-1'>
                    <h4 className='pt-5'>{props.art[props.art.id].title}</h4>
                    <p>{props.art[props.art.id].content}</p>
                    <p>가격: OFFER</p>
                    <button className='btn btn-danger'>주문하기</button>
                </div>
            </div>
        </div>
    );
}
export default Detail;
